
import{isDevMode}from'@angular/core';import{isArray,isBlank,isPresent,isString}from'../src/facade/lang';export function assertArrayOfStrings(identifier,value){if(!isDevMode()||isBlank(value)){return;}
if(!isArray(value)){throw new Error("Expected '"+identifier+"' to be an array of strings.");}
for(var i=0;i<value.length;i+=1){if(!isString(value[i])){throw new Error("Expected '"+identifier+"' to be an array of strings.");}}}
var INTERPOLATION_BLACKLIST_REGEXPS=[/^\s*$/,/[<>]/,/^[{}]$/,/&(#|[a-z])/i,/^\/\//,];export function assertInterpolationSymbols(identifier,value){if(isPresent(value)&&!(isArray(value)&&value.length==2)){throw new Error("Expected '"+identifier+"' to be an array, [start, end].");}
else if(isDevMode()&&!isBlank(value)){var start_1=value[0];var end_1=value[1];INTERPOLATION_BLACKLIST_REGEXPS.forEach(function(regexp){if(regexp.test(start_1)||regexp.test(end_1)){throw new Error("['"+start_1+"', '"+end_1+"'] contains unusable interpolation symbol.");}});}}